import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import { User, Lock, Eye, EyeOff } from 'lucide-react';

const LoginPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const { login } = useAuth();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const result = login(username, password);
      if (!result.success) {
        setError(result.error);
      }
    } catch (err) {
      setError('An error occurred during login');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex">
      {/* Left Section - Background Image with Text Overlay */}
      <div className="hidden lg:flex lg:w-3/5 relative bg-gradient-to-br from-blue-900 to-blue-700">
        {/* Background Pattern */}
        <div className="absolute inset-0 opacity-20">
          <div className="absolute inset-0 bg-gradient-to-br from-blue-800/50 to-transparent"></div>
          <div className="absolute top-10 left-10 w-32 h-32 border-4 border-white/20 rounded-full"></div>
          <div className="absolute bottom-20 right-20 w-24 h-24 border-4 border-white/20 rounded-full"></div>
          <div className="absolute top-1/2 left-1/4 w-16 h-16 border-4 border-white/20 rounded-full"></div>
        </div>
        
        {/* Main Title */}
        <div className="relative z-10 flex flex-col justify-center items-center text-center px-16">
          <div className="bg-white/10 backdrop-blur-sm rounded-2xl p-8 border border-white/20">
            <h1 className="text-5xl lg:text-6xl font-black text-white mb-6 font-montserrat tracking-wide">
              LOGISTICS 1
            </h1>
            <h2 className="text-2xl lg:text-3xl font-bold text-white/90 mb-4 font-poppins">
              SMART SUPPLY CHAIN &
            </h2>
            <h3 className="text-2xl lg:text-3xl font-bold text-white/90 font-poppins">
              PROCUREMENT MANAGEMENT
            </h3>
            <div className="mt-8 w-24 h-1 bg-white/60 mx-auto rounded-full"></div>
            <p className="text-white/80 text-lg mt-6 font-poppins">
              Advanced Hospital Logistics & Supply Chain Solutions
            </p>
          </div>
        </div>

        {/* Decorative Elements */}
        <div className="absolute top-20 right-20 w-2 h-2 bg-white/40 rounded-full animate-pulse"></div>
        <div className="absolute bottom-32 left-32 w-3 h-3 bg-white/30 rounded-full animate-pulse delay-300"></div>
        <div className="absolute top-1/3 right-1/3 w-1 h-1 bg-white/50 rounded-full animate-pulse delay-700"></div>
      </div>

      {/* Right Section - Login Form */}
      <div className="w-full lg:w-2/5 gradient-blue flex flex-col justify-center items-center px-8 py-12">
        <div className="w-full max-w-md">
          {/* Logo */}
          <div className="text-center mb-8">
            <div className="w-20 h-20 bg-white/20 rounded-full mx-auto mb-4 flex items-center justify-center backdrop-blur-sm">
              <div className="w-12 h-12 bg-white/30 rounded-full flex items-center justify-center">
                <span className="text-2xl font-bold text-white">L1</span>
              </div>
            </div>
            <h2 className="text-2xl font-bold text-white mb-2">Welcome Back</h2>
            <p className="text-white/80">Sign in to your account</p>
          </div>

          {/* Login Form */}
          <form onSubmit={handleSubmit} className="space-y-6">
            {/* Username Field */}
            <div className="relative">
              <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                <User className="h-5 w-5 text-white/60" />
              </div>
              <input
                type="text"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                placeholder="Username"
                className="w-full pl-10 pr-4 py-3 bg-white/10 border border-white/20 rounded-xl text-white placeholder-white/60 focus:outline-none focus:ring-2 focus:ring-white/30 focus:border-transparent backdrop-blur-sm"
                required
              />
            </div>

            {/* Password Field */}
            <div className="relative">
              <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                <Lock className="h-5 w-5 text-white/60" />
              </div>
              <input
                type={showPassword ? 'text' : 'password'}
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Password"
                className="w-full pl-10 pr-12 py-3 bg-white/10 border border-white/20 rounded-xl text-white placeholder-white/60 focus:outline-none focus:ring-2 focus:ring-white/30 focus:border-transparent backdrop-blur-sm"
                required
              />
              <button
                type="button"
                onClick={() => setShowPassword(!showPassword)}
                className="absolute inset-y-0 right-0 pr-3 flex items-center"
              >
                {showPassword ? (
                  <EyeOff className="h-5 w-5 text-white/60 hover:text-white/80" />
                ) : (
                  <Eye className="h-5 w-5 text-white/60 hover:text-white/80" />
                )}
              </button>
            </div>

            {/* Error Message */}
            {error && (
              <div className="bg-red-500/20 border border-red-500/30 rounded-xl p-3 text-red-100 text-sm">
                {error}
              </div>
            )}

            {/* Login Button */}
            <button
              type="submit"
              disabled={loading}
              className="w-full py-3 px-4 bg-white text-blue-600 font-semibold rounded-xl hover:bg-white/90 focus:outline-none focus:ring-2 focus:ring-white/30 transition-all duration-200 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              {loading ? (
                <div className="flex items-center justify-center">
                  <div className="w-5 h-5 border-2 border-blue-600 border-t-transparent rounded-full animate-spin mr-2"></div>
                  Signing In...
                </div>
              ) : (
                'Log In'
              )}
            </button>
          </form>

          {/* Help Link */}
          <div className="text-center mt-6">
            <a href="#" className="text-white/70 hover:text-white text-sm underline">
              Need Help?
            </a>
          </div>

          {/* Demo Credentials */}
          <div className="mt-8 p-4 bg-white/10 rounded-xl backdrop-blur-sm">
            <h3 className="text-white font-semibold mb-3 text-sm">Demo Credentials:</h3>
            <div className="space-y-2 text-xs text-white/80">
              <div><strong>Admin:</strong> admin / admin123</div>
              <div><strong>Manager:</strong> manager1 / manager123</div>
              <div><strong>Employee:</strong> employee1 / employee123</div>
              <div><strong>Procurement:</strong> procurement1 / procurement123</div>
              <div><strong>Project:</strong> project1 / project123</div>
              <div><strong>Maintenance:</strong> maintenance1 / maintenance123</div>
              <div><strong>Document:</strong> document1 / document123</div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
